import { Pipe, PipeTransform } from '@angular/core';
import { ALuDateAdapter } from '@lucca-front/ng/core';

@Pipe({
	name: 'luDate',
	pure: true,
})
export class LuDateAdapterPipe<D> implements PipeTransform {
	constructor(private _adapter: ALuDateAdapter<D>) {}
	transform(d: D, format: string = 'mediumDate'): string {
		if (d && this._adapter.isValid(d)) {
			return this._adapter.format(d, format);
		}
		return '';
	}
}

import { Pipe, PipeTransform, Inject, LOCALE_ID } from '@angular/core';
import { formatNumber, getLocaleNumberSymbol, NumberSymbol } from '@angular/common';

@Pipe({
	name: 'luNumber',
	pure: true,
})
export class LuNumberPipe implements PipeTransform {
	constructor(@Inject(LOCALE_ID) protected locale) {}
	transform(number: number, precision = 2) {
		const formatted = formatNumber(number, this.locale, `1.${precision}-${precision}`);
		const separator = getLocaleNumberSymbol(this.locale, NumberSymbol.Decimal);
		const split = formatted.split(separator);
		const integral = split[0];
		const decimal = split[1];
		const hideDecimal = Math.round(number) === number;
		if (precision > 0) {
			return `${integral}<span class="decimal-part${hideDecimal ? ' u-hidden' : ''}">${separator}${decimal}</span>`;
		} else {
			return integral;
		}

	}
}

import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeHtml, SafeStyle, SafeScript, SafeUrl, SafeResourceUrl } from '@angular/platform-browser';
import { LuSafeContentType } from './safe-content.model';

@Pipe({name: 'luSafeContent'})
export class LuSafeContentPipe implements PipeTransform {

	constructor(protected sanitizer: DomSanitizer) { }

	public transform(value: any, type: LuSafeContentType = 'html'): SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl {
		switch (type) {
			case 'html': return this.sanitizer.bypassSecurityTrustHtml(value);
			case 'style': return this.sanitizer.bypassSecurityTrustStyle(value);
			case 'script': return this.sanitizer.bypassSecurityTrustScript(value);
			case 'url': return this.sanitizer.bypassSecurityTrustUrl(value);
			case 'resourceUrl': return this.sanitizer.bypassSecurityTrustResourceUrl(value);
			default: throw new Error(`Invalid safe type specified: ${type}`);
		}
	}
}

import { Pipe, PipeTransform } from '@angular/core';
import { ILuUser } from '../user.model';
import {
	LuDisplayFullname,
	LuDisplayHybrid,
	LuDisplayInitials,
	LuDisplayFormat,
} from './display-format.model';
/**
 * Displays a user name according to specified format. Supported formats: f for first name,
 * F for first initial, l for last name, L for last initial.
 */
@Pipe({ name: 'luUserDisplay' })
export class LuUserDisplayPipe implements PipeTransform {
	transform(user: ILuUser, format: LuDisplayFormat = LuDisplayFullname.lastfirst): string {
		let result = '';

		if (!!user && !!user.firstName && !!user.lastName) {
			switch (format) {
				case LuDisplayFullname.lastfirst:
					result = user.lastName + ' ' + user.firstName;
					break;
				case LuDisplayFullname.firstlast:
					result = user.firstName + ' ' + user.lastName;
					break;
				case LuDisplayFullname.first:
					result = user.firstName;
					break;
				case LuDisplayFullname.last:
					result = user.lastName;
					break;
				case LuDisplayInitials.lastfirst:
					result = user.lastName.charAt(0) + user.firstName.charAt(0);
					break;
				case LuDisplayInitials.firstlast:
					result = user.firstName.charAt(0) + user.lastName.charAt(0);
					break;
				case LuDisplayInitials.first:
					result = user.firstName.charAt(0);
					break;
				case LuDisplayInitials.last:
					result = user.lastName.charAt(0);
					break;
				case LuDisplayHybrid.firstIlastFull:
					result = user.firstName.charAt(0) + '. ' + user.lastName;
					break;
				case LuDisplayHybrid.lastIfirstFull:
					result = user.lastName.charAt(0) + '. ' + user.firstName;
					break;
				case LuDisplayHybrid.lastFullfirstI:
					result = user.lastName + ' ' + user.firstName.charAt(0) + '.';
					break;
				case LuDisplayHybrid.firstFulllastI:
					result = user.firstName + ' ' + user.lastName.charAt(0) + '.';
					break;
				default:
					break;
			}
		}
		return result;
	}
}

