import { Injectable, LOCALE_ID, Inject } from '@angular/core';
import { LOL_TRANSLATIONS_TOKEN } from './lol.translate';

@Injectable()
export class LolIntl {
	lol;
	constructor(@Inject(LOL_TRANSLATIONS_TOKEN) translations, @Inject(LOCALE_ID) locale) {
		const fallback = translations['en'] || {};
		const current = translations[locale] || translations[locale.substring(0, 2)] || {};

		const all = { ...fallback, ...current };
		Object.keys(all).forEach(k => {
			Object.defineProperty(this, k, {
				get: () => all[k],
			});
		});
	}
}

import { Injectable } from '@angular/core';
import { ILuApiItem } from '../api.model';
import { ALuApiService } from './api-service.model';
import { LuApiV3Service } from './api-v3.service';
import { LuApiV4Service } from './api-v4.service';
import { Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';

@Injectable()
export class LuApiHybridService<T extends ILuApiItem = ILuApiItem> extends ALuApiService<T> {
	private _v3Service: LuApiV3Service<T>;
	private _v4Service: LuApiV4Service<T>;

	private _standard = 'v3';
	set standard(std: string) { this._standard = std; }

	// both
	set api(api: string) {
		this._v3Service.api = api;
		this._v4Service.api = api;
	}
	set filters(filters: string[]) {
		this._v3Service.filters = filters;
		this._v4Service.filters = filters;
	}

	// api v3 only
	set fields(fields: string) { this._v3Service.fields = fields; }
	set orderBy(orderBy: string) { this._v3Service.orderBy = orderBy; }

	// api v4 only
	set sort(sort: string) {
		this._v4Service.sort = sort;
	}

	private get _service(): ALuApiService<T> {
		switch(this._standard) {
			case 'v4':
				return this._v4Service;
			case 'v3':
			default:
				return this._v3Service;
		}
	}

	constructor(
		private _http: HttpClient,
	) {
		super();
		this._v3Service = new LuApiV3Service(this._http);
		this._v4Service = new LuApiV4Service(this._http);
	}

	getAll(filters: string[] = []): Observable<T[]> {
		return this._service.getAll(filters);
	}

	getPaged(page: number, filters: string[] = []): Observable<T[]> {
		return this._service.getPaged(page, filters);
	}

	searchAll(clue: string, filters: string[] = []): Observable<T[]> {
		return this._service.searchAll(clue, filters);
	}

	searchPaged(clue: string, page: number, filters: string[] = []): Observable<T[]> {
		return this._service.searchPaged(clue, page, filters);
	}
}

import { Injectable } from '@angular/core';
import { ILuApiItem, ILuApiCollectionResponse } from '../api.model';
import { ALuApiService } from './api-service.model';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

const MAGIC_PAGE_SIZE = 20;

@Injectable()
export class LuApiV3Service<T extends ILuApiItem = ILuApiItem> extends ALuApiService<T> {
	protected _api: string;
	set api(api: string) { this._api = api; }
	protected _fields = 'fields=id,name';
	set fields(fields: string) { this._fields = `fields=${fields}`; }
	protected _filters: string[] = [];
	set filters(filters: string[]) { this._filters = filters || []; }
	protected _orderBy = 'orderBy=name,asc';
	set orderBy(orderBy: string) { this._orderBy = `orderBy=${orderBy}`; }

	get url() {
		return `${this._api}?${[...this._filters, this._orderBy, this._fields].filter(f => !!f).join('&')}`;
	}

	constructor(protected _http: HttpClient) { super(); }

	getAll(filters: string[] = []): Observable<T[]> {
		return this._get([this.url, ...filters].join('&'));
	}

	getPaged(page: number, filters: string[] = []): Observable<T[]> {
		const paging = `paging=${page * MAGIC_PAGE_SIZE},${MAGIC_PAGE_SIZE}`;
		const url = [this.url, paging, ...filters].join('&');
		return this._get(url);
	}

	searchAll(clue: string, filters: string[] = []): Observable<T[]> {
		if (!clue) {
			return this.getAll(filters);
		}
		const url = [this.url, this._clueFilter(clue), ...filters].join('&');
		return this._get(url);
	}

	searchPaged(clue: string, page: number, filters: string[] = []): Observable<T[]> {
		if (!clue) {
			return this.getPaged(page, filters);
		}
		const paging = `paging=${page * MAGIC_PAGE_SIZE},${MAGIC_PAGE_SIZE}`;
		const url = [this.url, this._clueFilter(clue), paging, ...filters].join('&');
		return this._get(url);
	}

	protected _get(url): Observable<T[]> {
		return this._http.get<ILuApiCollectionResponse<any>>(url)
		.pipe(
			map(response => response.data.items)
		);
	}
	protected _clueFilter(clue) {
		const urlSafeClue = encodeURIComponent(clue);
		return `name=like,${urlSafeClue}`;
	}
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { ILuApiItem } from '../api.model';
import { ALuApiService } from './api-service.model';

@Injectable()
export class LuApiV4Service<T extends ILuApiItem = ILuApiItem> extends ALuApiService<T> {
	protected _api: string;
	set api(api: string) { this._api = api; }

	protected _filters: string[] = [];
	set filters(filters: string[]) { this._filters = filters || []; }
	get filters(): string[] { return this._filters; }

	protected _sort: string;
	set sort(sort: string) { this._sort = `sort=${sort}`; }

	constructor(protected _http: HttpClient) { super(); }

	getAll(filters: string[] = []): Observable<T[]> {
		const query = [...this.filters, ...filters, this._sort].filter(f => !!f);
		const url = [this._api, query.join('&')].join('?');
		return this._http.get<{ items: T[] }>(url).pipe(
			map(res => res.items),
		);
	}
	getPaged(page: number = 0, filters: string[] = []): Observable<T[]> {
		const query = [`page=${page + 1}`, ...this.filters, ...filters, this._sort].filter(f => !!f);
		const url = [this._api, query.join('&')].join('?');
		return this._http.get<{ items: T[] }>(url).pipe(
			map(res => res.items),
		);
	}
	searchAll(clue: string = '', filters: string[] = []): Observable<T[]> {
		if (!clue) {
			return this.getAll(filters);
		}
		const urlSafeClues = clue.split(' ').map(c => encodeURIComponent(c));
		const query = [`search=${urlSafeClues}`, ...this.filters, ...filters, this._sort].filter(f => !!f);
		const url = [this._api, query.join('&')].join('?');
		return this._http.get<{ items: T[] }>(url).pipe(
			map(res => res.items),
		);
	}
	searchPaged(clue: string = '', page: number = 0, filters: string[] = []): Observable<T[]> {
		if (!clue) {
			return this.getPaged(page, filters);
		}
		const urlSafeClues = clue.split(' ').map(c => encodeURIComponent(c));
		const query = [`search=${urlSafeClues}`, `page=${page + 1}`, ...this.filters, ...filters, this._sort].filter(f => !!f);
		const url = [this._api, query.join('&')].join('?');
		return this._http.get<{ items: T[] }>(url).pipe(
			map(res => res.items),
		);
	}
}

import { Injectable } from '@angular/core';
import { DayItem, MonthItem, YearItem, DecadeItem } from './calendar-item.class';
import { ALuDateAdapter } from '@lucca-front/ng/core';

@Injectable()
export class LuCalendarItemFactory<D> {
	constructor(
		private _adapter: ALuDateAdapter<D>,
	) {}
	forgeDay(d: D, format = 'd'): DayItem<D> {
		const date = this._adapter.clone(d);
		return new DayItem(date, this._adapter.format(date, format));
	}
	forgeMonth(d: D, format = 'MMM'): MonthItem<D> {
		const year = this._adapter.getYear(d);
		const month = this._adapter.getMonth(d);
		const date = this._adapter.getDate(d);
		const monthStart = this._adapter.forge(year, month, 1);
		return new MonthItem(monthStart, this._adapter.format(monthStart, format));
	}
	forgeYear(d: D, format = 'y'): YearItem<D> {
		const year = this._adapter.getYear(d);
		const yearStart = this._adapter.forge(year, 1, 1);
		return new YearItem(yearStart, this._adapter.format(yearStart, format));
	}
	forgeDecade(d: D, format = 'y'): DecadeItem<D> {
		const year = this._adapter.getYear(d);
		const decadeStart = this._adapter.forge(10 * Math.floor(year / 10), 1, 1);
		const decadeEnd = this._adapter.forge(10 * Math.floor(year / 10) + 9, 1, 1);
		const label = `${this._adapter.format(decadeStart, format)} - ${this._adapter.format(decadeEnd, format)}`;
		return new DecadeItem(decadeStart, label);
	}
}

