import { ILuApiItem } from '../../api.model';
import { ILuApiOptionFeeder } from '../feeder/index';
import { ILuOnOpenSubscriber, ILuOnScrollBottomSubscriber } from '@lucca-front/ng/core';
import { ILuApiService } from '../../service/index';

export interface ILuApiOptionSearcher<T extends ILuApiItem = ILuApiItem> extends ILuApiOptionFeeder<T> {}

export abstract class ALuApiOptionSearcher<T extends ILuApiItem = ILuApiItem, S extends ILuApiService<T> = ILuApiService<T>>
implements ILuApiOptionFeeder<T>, ILuOnOpenSubscriber {
	outOptions$ = new Subject<T[]>();
	loading$: Observable<boolean>;
	empty$: Observable<boolean>;

	protected _clue$: Observable<string>;

	set clue$(clue$: Observable<string>) {
		// this.initObservables(clue$);
		this._clue$ = clue$;
	}
	constructor(protected _service: S) {}
	init() {
		this.initObservables();
	}
	onOpen() {
		this.resetClue();
	}
	onClose() {
		this.clearOptions();
	}
	protected initObservables() {
		// this._clue$ = clue$.pipe(share());
		const results$ = this._clue$
		.pipe(
			switchMap(clue => this._service.searchAll(clue)),
			catchError(err => of([])),
			share(),
		);

		results$.subscribe(items => this.outOptions$.next(items));
		this.loading$ = merge(
			this._clue$.pipe(mapTo(true)),
			results$.pipe(mapTo(false)),
		);
		this.empty$ = results$.pipe(
			map(o => o.length === 0),
		);
	}
	abstract resetClue();
	protected clearOptions() {
		this.outOptions$.next([]);
	}
}

export interface ILuApiOptionPagedSearcher<T extends ILuApiItem = ILuApiItem> extends ILuApiOptionSearcher<T> {}

export abstract class ALuApiOptionPagedSearcher<T extends ILuApiItem = ILuApiItem, S extends ILuApiService<T> = ILuApiService<T>>
extends ALuApiOptionSearcher<T, S>
implements ILuApiOptionPagedSearcher<T>, ILuOnScrollBottomSubscriber {
	outOptions$ = new Subject<T[]>();
	loading$: Observable<boolean>;
	protected _loading = false;
	protected _page$ = new Subject<number>();
	protected _page: number;
	protected _options: T[] = [];

	constructor(service: S) {
		super(service);
	}
	onOpen() {
		this.resetClue();
		this.resetPage();
	}
	onScrollBottom() {
		if (!this._loading) {
			this._page$.next(this._page + 1);
		}
	}

	protected initObservables() {
		const distinctPage$ = this._page$.pipe(
			distinctUntilChanged(),
			tap(p => this._page = p),
		);

		// this._clue$ = clue$.pipe(
		// 	tap(() => this._page$.next(0)),
		// 	// distinctUntilChanged(),
		// 	share()
		// );
		const results$ = combineLatest(
			distinctPage$,
			this._clue$,
		).pipe(
			debounceTime(100),
			switchMap(([page, clue]) => this._service.searchPaged(clue, page)),
			catchError(err => of([])),
			share(),
		);

		results$
		.pipe(withLatestFrom(distinctPage$))
		.subscribe(([items, page]) => {
			if (page === 0) {
				this._options = [...items];
			} else {
				this._options.push(...items);
			}
			this.outOptions$.next([...this._options]);
		});
		this.loading$ = merge(
			this._clue$.pipe(mapTo(true)),
			results$.pipe(mapTo(false)),
		);
		this.loading$.subscribe(l => this._loading = l);
		this.empty$ = this.outOptions$.pipe(
			map(o => o.length === 0),
		);
	}
	abstract resetClue();
	resetPage() {
		this._page$.next(0);
	}
}

import { ILuOnOpenSubscriber, ILuOnScrollBottomSubscriber } from '@lucca-front/ng/core';
import { ILuOptionOperator } from '@lucca-front/ng/option';
import { ILuApiItem } from '../../api.model';
import { Observable, of, merge, Subject } from 'rxjs';
import { switchMap, catchError, mapTo, tap, map, distinctUntilChanged } from 'rxjs/operators';
import { ILuApiService } from '../../service/index';

enum Strategy {
	append,
	replace,
}
const MAGIC_PAGE_SIZE = 20;
export interface ILuApiOptionPager<T extends ILuApiItem = ILuApiItem> extends ILuOptionOperator<T> {}
export interface ILuApiPagerService<T extends ILuApiItem = ILuApiItem> {
	getPaged(page: number): Observable<T[]>;
}

export abstract class ALuApiOptionPager<T extends ILuApiItem = ILuApiItem, S extends ILuApiService<T> = ILuApiService<T>>
implements ILuApiOptionPager<T>, ILuOnOpenSubscriber, ILuOnScrollBottomSubscriber {
	outOptions$ = new Subject<T[]>();
	loading$: Observable<boolean>;

	protected _loading = false;
	protected _results$: Observable<{ items: T[], strategy: Strategy }>;
	protected _options: T[] = [];
	protected _page$ = new Subject<number>();
	protected _page: number;
	protected _initialized = false;
	constructor(protected _service: S) {
	}
	protected init() {
		this.initObservables();
	}
	onOpen() {
		if (!this._initialized) {
			this._page$.next(0);
			this._initialized = true;
		}
	}
	// onClose() {
	// 	this._page$.next(0);
	// }
	onScrollBottom() {
		if (!this._loading) {
			this._page$.next(this._page + 1);
		}
	}
	protected initObservables() {
		const _results$: Observable<{ items: T[], strategy: Strategy }> = this._page$
		.pipe(
			distinctUntilChanged(),
			tap(p => this._page = p),
			switchMap(page => {
				if (page === undefined) {
					return of({ items: [], strategy: Strategy.replace });
				}
				return this._service.getPaged(page).pipe(
					map(items => ({ items: items, strategy: page === 0 ? Strategy.replace : Strategy.append }))
				);
			}),
			catchError(err => of({ items: [], strategy: Strategy.replace })),
			tap(results => {
				if (results.strategy === Strategy.replace) {
					this._options = [...results.items];
				} else {
					this._options.push(...results.items);
				}
				this.outOptions$.next([...this._options]);
			}),
		);

		this.loading$ = merge(
			this._page$.pipe(mapTo(true)),
			_results$.pipe(mapTo(false)),
		);
		this.loading$.subscribe(l => this._loading = l);
	}
}

