			}

			/** Only subscribe to mouse enter/leave of the panel if trigger = hover */

			this._initPopover();
			this._emitOpen();
		}
	}

	/** Closes the popover. */
	closePopover(): void {
		if (this._overlayRef && this._overlayRef.hasAttached()) {
			this._overlayRef.detach();

			/** unsubscribe to backdrop click if it was defined */
			if (!!this._backdropSubscription) {
				// if (this.popover.triggerEvent === 'click') {
				this._backdropSubscription.unsubscribe();
			}

			this._resetPopover();
			this._emitClose();
		}
	}

	/** Removes the popover from the DOM. */
	destroyPopover(): void {
		if (this._overlayRef) {
			this._overlayRef.dispose();
			this._overlayRef = null;
			this._cleanUpSubscriptions();
		}
	}

	/** Focuses the popover trigger. */
	focus() {
		this._elementRef.nativeElement.focus();
	}

	_handleMousedown(event: MouseEvent): void {
		if (!isFakeMousedownFromScreenReader(event)) {
			this._openedByMouse = true;
		}
	}

	/** The text direction of the containing app. */
	get dir(): Direction {
		// return this._dir && this._dir.value === 'rtl' ? 'rtl' : 'ltr';
		return 'ltr';
	}

	/** Return if the popover main positionning is vertical */
	get isVerticallyPositionned(): boolean {
		const position = this.target.position;
		return (
			position === 'below' || position === 'above'
		);
	}

	/**
	 * This method ensures that the popover
	 */
	protected _subscribeToPanelEvents(): void {
		if (this._overlayRef) {
			this._panelEventsSubscriptions = new Subscription();
			if (this.triggerEvent === 'hover') {

				this._panelEventsSubscriptions.add(
					this.panel.hovered
					.subscribe((hovered) => {
						this._hovered$.next(hovered);
					}),
				);
			}
			this._panelEventsSubscriptions.add(
				this.panel.close
				.subscribe(() => {
					this.closePopover();
				}),
			);
			this._panelEventsSubscriptions.add(
				this.panel.open
				.subscribe(() => {
					this.openPopover();
				}),
			);
		}
	}
		/**
	 * This method ensures that the popover closes when the overlay backdrop is clicked.
	 * We do not use first() here because doing so would not catch clicks from within
	 * the popover, and it would fail to unsubscribe properly. Instead, we unsubscribe
	 * explicitly when the popover is closed or destroyed.
	 */
	protected _subscribeToBackdrop(): void {
		if (this._overlayRef) {
			this._backdropSubscription = this._overlayRef
				.backdropClick()
				.subscribe(() => {
					this.closePopover();
				});
		}
	}

	/**
	 * This method sets the popover state to open and focuses the first item if
	 * the popover was opened via the keyboard.
	 */
	protected _initPopover(): void {
		this._setIsPopoverOpen(true);
		this.panel.panelId = this._panelId;
		this.panel.triggerId = this._triggerId;
		this.panel.keydownEvents$ = this._overlayRef.keydownEvents();
		this._subscribeToPanelEvents();
	}

	/**
	 * This method resets the popover when it's closed, most importantly restoring
	 * focus to the popover trigger if the popover was opened via the keyboard.
	 */
	protected _resetPopover(): void {
		this._setIsPopoverOpen(false);

		// Focus only needs to be reset to the host element if the popover was opened
		// by the keyboard and manually shifted to the first popover item.
		if (!this._openedByMouse && this.triggerEvent === 'click') {
			this.focus();
		}
		this._openedByMouse = false;
	}

	/** set state rather than toggle to support triggers sharing a popover */
	protected _setIsPopoverOpen(isOpen: boolean): void {
		this._popoverOpen = isOpen;
		// tell the panel it's opening/closing
		isOpen ? this.panel.onOpen() : this.panel.onClose();
	}

	/**
	 *  This method checks that a valid instance of MdPopover has been passed into
	 *  mdPopoverTriggerFor. If not, an exception is thrown.
	 */
	protected _checkPanel() {
		if (!this.panel) {
			throwLuPopoverMissingPanelError();
		}
	}
	protected _checkTarget() {
		if (!this.target) {
			throwLuPopoverMissingTargetError();
		}
	}

	/**
	 *  This method creates the overlay from the provided popover's template and saves its
	 *  OverlayRef so that it can be attached to the DOM when openPopover is called.
	 */
	protected _createOverlay(): OverlayRef {
		if (!this._overlayRef) {
			this._portal = new TemplatePortal(
				this.panel.templateRef,
				this._viewContainerRef,
			);
			const config = this._getOverlayConfig();
			this._subscribeToPositions(
				config.positionStrategy as ConnectedPositionStrategy,
			);
			this._overlayRef = this._overlay.create(config);
		}

		return this._overlayRef;
	}

	/**
	 * This method builds the configuration object needed to create the overlay, the OverlayConfig.
	 * @returns OverlayConfig
	 */
	protected _getOverlayConfig(): OverlayConfig {
		const overlayState = new OverlayConfig();
		overlayState.positionStrategy = this._getPosition().withDirection(this.dir);
		/** Display overlay backdrop if trigger event is click */
		if (this.triggerEvent === 'click') {
			overlayState.hasBackdrop = true;
			overlayState.backdropClass = 'cdk-overlay-transparent-backdrop';
		}

		overlayState.direction = this.dir;
		const scrollStrategy = this.panel.scrollStrategy;
		switch (scrollStrategy) {
			case 'block':
				overlayState.scrollStrategy = this._overlay.scrollStrategies.block();
				break;

			case 'close':
				overlayState.scrollStrategy = this._overlay.scrollStrategies.close();
				break;

			default:
				overlayState.scrollStrategy = this._overlay.scrollStrategies.reposition();
				break;
		}
		return overlayState;
	}

	/**
	 * Listens to changes in the position of the overlay and sets the correct classes
	 * on the popover based on the new position. This ensures the animation origin is always
	 * correct, even if a fallback position is used for the overlay.
	 */
	protected _subscribeToPositions(position: ConnectedPositionStrategy): void {
		this._positionSubscription = position.onPositionChange.pipe(
			map(c => c.connectionPair),
			distinctUntilChanged(),
		)
		.subscribe(connectionPair => {
			this.panel.setPositionClasses(connectionPair.overlayX, connectionPair.overlayY);
		});
	}

	/**
	 * This method builds the position strategy for the overlay, so the popover is properly connected
	 * to the trigger.
	 * @returns ConnectedPositionStrategy
	 */
	protected _getPosition(): ConnectedPositionStrategy {
		const connectionPosition: OriginConnectionPosition = {
			originX: 'start',
			originY: 'top',
		};

		// Position
		const position = this.target.position;
		const overlap = this.target.overlap;
		if (position === 'above') {
			connectionPosition.originY = overlap ? 'bottom' : 'top';
		} else if (position === 'below') {
			connectionPosition.originY = overlap ? 'top' : 'bottom';
		} else if (position === 'before') {
			connectionPosition.originX = overlap ? 'end' : 'start';
		} else if (position === 'after') {
			connectionPosition.originX = overlap ? 'start' : 'end';
		}

		// Alignment
		const alignment = this.target.alignment;
		if (this.isVerticallyPositionned) {
			if (alignment === 'left') {
				connectionPosition.originX = 'start';
			} else if (alignment === 'right') {
				connectionPosition.originX = 'end';
			} else {
				connectionPosition.originX = 'center';
			}
		} else {
			if (alignment === 'top') {
				connectionPosition.originY = 'top';
			} else if (alignment === 'bottom') {
				connectionPosition.originY = 'bottom';
			} else {
				connectionPosition.originY = 'center';
			}
		}

		const overlayPosition: OverlayConnectionPosition = {
			overlayX: 'start',
			overlayY: 'top',
		};

		if (overlap) {
			overlayPosition.overlayX = connectionPosition.originX;
			overlayPosition.overlayY = connectionPosition.originY;
		} else if (this.isVerticallyPositionned) {
			overlayPosition.overlayX = connectionPosition.originX;
			overlayPosition.overlayY =
				position === 'above' ? 'bottom' : 'top';
		} else {
			overlayPosition.overlayX =
				position === 'before' ? 'end' : 'start';
			overlayPosition.overlayY = connectionPosition.originY;
		}


		const offsetX = this.target.offsetX;
		const offsetY = this.target.offsetY;

		/**
		 * For overriding position element, when LuPopoverTargetAt has a valid element reference.
		 * Useful for sticking popover to parent element and offsetting arrow to trigger element.
		 * If undefined defaults to the trigger element reference.
		 */
		const element = this.target.elementRef;
		// if (typeof this.targetElement !== 'undefined' && this.popover) {
		// 	this.popover.containerPositioning = true;
		// 	element = this.targetElement._elementRef;
		// }

		/**
		 * TODO: Updates when withFallbackPosition takes individual offsets
		 */

		return this._overlay
			.position()
			.connectedTo(element, connectionPosition, overlayPosition)
			.withFallbackPosition(
				{
					originX: connectionPosition.originX,
					originY: this._invertVerticalPos(connectionPosition.originY),
				},
				{
					overlayX: overlayPosition.overlayX,
					overlayY: this._invertVerticalPos(overlayPosition.overlayY),
				},
			)
			.withFallbackPosition(
				{
					originX: this._invertHorizontalPos(connectionPosition.originX),
					originY: connectionPosition.originY,
				},
				{
					overlayX: this._invertHorizontalPos(overlayPosition.overlayX),
					overlayY: overlayPosition.overlayY,
				},
			)
			.withFallbackPosition(
				{
					originX: this._invertHorizontalPos(connectionPosition.originX),
					originY: this._invertVerticalPos(connectionPosition.originY),
				},
				{
					overlayX: this._invertHorizontalPos(overlayPosition.overlayX),
					overlayY: this._invertVerticalPos(overlayPosition.overlayY),
				},
			)
			.withOffsetX(offsetX)
			.withOffsetY(offsetY);
	}

	protected _invertVerticalPos(y: VerticalConnectionPos) {
		if (y === 'top') {
			y = 'bottom';
		} else if (y === 'bottom') {
			y = 'top';
		}
		return y;
	}

	protected _invertHorizontalPos(x: HorizontalConnectionPos) {
		if (x === 'end') {
			x = 'start';
		} else if (x === 'start') {
			x = 'end';
		}

		return x;
	}

	protected _cleanUpSubscriptions(): void {
		if (this._backdropSubscription) {
			this._backdropSubscription.unsubscribe();
		}
		if (this._positionSubscription) {
			this._positionSubscription.unsubscribe();
		}
		if (this._hoveredSubscription) {
			this._hoveredSubscription.unsubscribe();
		}
		if (this._panelEventsSubscriptions) {
			this._panelEventsSubscriptions.unsubscribe();
		}
	}
}

import { Observable, Subject, Subscription, merge } from 'rxjs';
import { Overlay, OverlayRef, OverlayConfig } from '@angular/cdk/overlay';
import { ComponentPortal, PortalInjector, ComponentType } from '@angular/cdk/portal';
import { first, filter } from 'rxjs/operators';
import { ComponentRef } from '@angular/core';
import { ESCAPE } from '@angular/cdk/keycodes';
import { Injector } from '@angular/core';
import { LU_POPUP_DATA } from './popup.token';
import { ILuPopupConfig } from './popup-config.model';
import { ILuPopupContent } from './popup.model';

export interface ILuPopupRef<T extends ILuPopupContent = ILuPopupContent, D = any, R = any> {
	onOpen: Observable<D>;
	onClose: Observable<R>;
	onDismiss: Observable<void>;
	open(data: D): void;
	close(result: R): void;
	dismiss(): void;
}
export interface ILuPopupRefFactory<TComponent = any, TConfig extends ILuPopupConfig = ILuPopupConfig> {
	forge<T extends TComponent, C extends TConfig>(component: ComponentType<T>, config: C): ILuPopupRef<T>;
}

export abstract class ALuPopupRef<T extends ILuPopupContent = ILuPopupContent, D = any, R = any> implements ILuPopupRef<T, D, R> {
	onOpen = new Subject<D>();
	onClose = new Subject<R>();
	onDismiss = new Subject<void>();

	protected _overlayRef: OverlayRef;
	protected _componentRef: ComponentRef<T>;

	protected _subs = new Subscription();

	constructor(
		protected _overlay: Overlay,
		protected _injector: Injector,
		protected _component: ComponentType<T>,
		protected _config: ILuPopupConfig,
	) {}


	open(data?: D) {
		this._createOverlay();
		this._openPopup(data);

		this._subToCloseEvents();

		this.onOpen.next(data);
		this.onOpen.complete();
	}
	close(result?: R) {
		this.onClose.next(result);
		this._destroy();
	}
	dismiss() {
		this.onDismiss.next();
		this._destroy();
	}
	/**
	 *  This method creates the overlay from the provided popover's template and saves its
	 *  OverlayRef so that it can be attached to the DOM when openPopover is called.
	 */
	protected _createOverlay() {
		if (!this._overlayRef) {
			const overlayConfig = this._getOverlayConfig();
			this._overlayRef = this._overlay.create(overlayConfig);
		}
	}
		/**
	 * This method builds the configuration object needed to create the overlay, the OverlayConfig.
	 * @returns OverlayConfig
	 */
	protected _getOverlayConfig(): OverlayConfig {
		const overlayConfig = new OverlayConfig();
		switch (this._config.position) {
			case 'top':
				overlayConfig.positionStrategy =  this._overlay.position().global().centerHorizontally().top('0');
				break;
			case 'bottom':
				overlayConfig.positionStrategy =  this._overlay.position().global().centerHorizontally().bottom('0');
				break;
			case 'left':
				overlayConfig.positionStrategy =  this._overlay.position().global().centerVertically().left('0');
				break;
			case 'right':
				overlayConfig.positionStrategy =  this._overlay.position().global().centerVertically().right('0');
				break;

			case 'center':
			default:
				overlayConfig.positionStrategy =  this._overlay.position().global().centerHorizontally().centerVertically();
				break;
		}
		overlayConfig.hasBackdrop = !this._config.noBackdrop;
		overlayConfig.backdropClass = this._config.backdropClass;
		const panelClasses = [];
		if (Array.isArray(this._config.panelClass)) {
			panelClasses.push(...this._config.panelClass);
		} else {
			panelClasses.push(this._config.panelClass);
		}
		panelClasses.push(`size-${this._config.size}`);
		overlayConfig.panelClass = panelClasses;
		overlayConfig.scrollStrategy = this._overlay.scrollStrategies.block();
		return overlayConfig;
	}
	protected _openPopup(data?: D) {
		const injectionMap = new WeakMap();
		injectionMap.set(ALuPopupRef, this);
		injectionMap.set(LU_POPUP_DATA, data);
		const injector = new PortalInjector(this._injector, injectionMap);
		const portal = new ComponentPortal(this._component, undefined, injector);
		this._componentRef = this._overlayRef.attach<T>(portal);
	}

	protected _destroy() {
		this._cleanSubscription();
		this._closePopup();
		this._destroyOverlay();
		this._completeSubjects();

	}
	_completeSubjects() {
		this.onClose.complete();
		this.onOpen.complete();
		this.onDismiss.complete();
	}

	protected _destroyOverlay() {
		this._overlayRef.detachBackdrop();
		this._overlayRef.detach();
	}
	protected _closePopup() {
		this._componentRef.destroy();
	}
	protected _subToCloseEvents() {
		if (!this._config.undismissable) {

			const bdClicked$ = this._overlayRef.backdropClick();
			const escPressed$ = this._overlayRef.keydownEvents().pipe(
				filter(evt => evt.keyCode === ESCAPE),
			);
			const sub = merge(bdClicked$, escPressed$).pipe(first())
			.subscribe(e => this.dismiss());
			this._subs.add(sub);
		}
	}
	protected _cleanSubscription() {
		this._subs.unsubscribe();
	}
}

import {
	ChangeDetectorRef,
	ViewContainerRef,
	ElementRef,
	Renderer2,
} from '@angular/core';
import { ControlValueAccessor } from '@angular/forms';
import { ALuPopoverTrigger, LuPopoverTarget, ILuPopoverTarget } from '@lucca-front/ng/popover';
import { Overlay, OverlayConfig } from '@angular/cdk/overlay';
import { ILuClearer, ILuInput, ILuInputDisplayer } from '@lucca-front/ng/input';
import { ILuInputWithPicker, ILuPickerPanel } from '@lucca-front/ng/picker';
import { Subscription } from 'rxjs';

export abstract class ALuSelectInput<T = any, TPicker extends ILuPickerPanel<T> = ILuPickerPanel<T>>
extends ALuPopoverTrigger<TPicker>
implements ControlValueAccessor, ILuInputWithPicker<T>, ILuInput<T> {
	protected _subs = new Subscription();
	constructor(
		protected _changeDetectorRef: ChangeDetectorRef,
		protected _overlay: Overlay,
		protected _elementRef: ElementRef,
		protected _viewContainerRef: ViewContainerRef,
		protected _renderer: Renderer2,
	) {
		super(
			_overlay,
			_elementRef,
			_viewContainerRef,
		);
		this.target = new LuPopoverTarget() as ILuPopoverTarget;
		this.target.elementRef = this._elementRef;
		this.target.position = 'below';
		this.target.alignment = 'left';
	}
	protected _isContentInitialized = false;
	protected _placeholder: string;
	get placeholder() { return this._placeholder; }
	set placeholder(p: string) { this._placeholder = p; }
	/**
	 * contriol value accessor interface implementation
	 */
	protected _value: T | T[];
	setValue(value: T | T[]) {
		if (this.disabled) {
			return;
		}
		this.value = value;
		this._cvaOnChange(value);
		this._onTouched();
	}
	get value(): T | T[] {
		return this._value;
	}
	set value(value: T | T[]) {
		this._value = value;
		this.render();
		this.applyClasses();
		if (!!this._picker) {
			this._picker.setValue(value);
		}
		this._changeDetectorRef.markForCheck();
	}
	// From ControlValueAccessor interface
	writeValue(value: T | T[]) {
		this.value = value;
	}
	// From ControlValueAccessor interface
	protected _cvaOnChange = (v: T | T[]) => {};
	registerOnChange(fn: any) {
		this._cvaOnChange = fn;
	}
	// From ControlValueAccessor interface
	protected _onTouched = () => {};
	registerOnTouched(fn: any) {
		this._onTouched = fn;
	}
	set disabled(d) { this._disabled = d; }
	get disabled() { return this._disabled; }
	setDisabledState(disabled: boolean) {
		this.disabled = disabled;
		this._changeDetectorRef.markForCheck();
	}
	protected isEmpty() {
		const isEmptyArray = Array.isArray(this.value) && this.value.length === 0;
		return this.value === null || this.value === undefined || isEmptyArray;
	}
	protected applyClasses() {
		if (this.isEmpty()) {
			this._renderer.removeClass(this._elementRef.nativeElement, 'is-filled');
		} else {
			this._renderer.addClass(this._elementRef.nativeElement, 'is-filled');
		}
	}
	/**
	 * popover trigger class extension
	 */
	protected set _picker(picker: TPicker) {
		this.panel = picker;
		picker.multiple = this._multiple;
		this.subToPickerEvts();
	}
	protected get _picker() { return this.panel; }
	protected set _clearer(clearer: ILuClearer<T>) {
		if (!!clearer && !!clearer.onClear) {
			this._subs.add(clearer.onClear.subscribe(value => this.setValue(value)));
		}
	}
	protected subToPickerEvts() {
		if (!!this.panel) {
			this._subs.add(this.panel.onSelectValue.subscribe(value => this.setValue(value)));
		}
	}

	closePopover() {
		this._onTouched();
		super.closePopover();
	}

	onDestroy() {
		if (this._popoverOpen) {
			this.closePopover();
		}
		this.destroyPopover();
		this._subs.unsubscribe();
		this._cleanUpSubscriptions();

	}

	protected _getOverlayConfig(): OverlayConfig {
		const config = super._getOverlayConfig();
		const clientRect = this._elementRef.nativeElement.getBoundingClientRect();
		config.minWidth = `${Math.max(185, clientRect.width)}px`; // might become min/maxWidth
		return config;
	}

	/* Rendering via a inpt displayer */
	protected _displayer: ILuInputDisplayer<T>;
	protected _displayContainer: ViewContainerRef;
	protected set displayer(d: ILuInputDisplayer<T>) {
		this._displayer = d;
		this.render();
	}
	protected set displayContainer(vcr: ViewContainerRef) {
		this._displayContainer = vcr;
	}
	protected render() {
		if (!this._displayer || !this._isContentInitialized) { return; }
		if (this.useMultipleViews()) {
			this.renderMultipleViews();
		} else {
			this.renderSingleView();
		}
	}
	protected useMultipleViews() {
		return this._multiple && !!this._displayer && !this._displayer.multiple;
	}

	protected renderSingleView() {
		this.clearDisplay();
		if (this.value !== null && this.value !== undefined) {
			const newView = this.getView(this.value);
			this.displayView(newView);
		}
	}
	protected clearDisplay() {
		this._displayContainer.clear();
	}
	protected getView(value: T | T[]) {
		if (!!this._displayer) {
			return this._displayer.getViewRef(value);
		}
		return undefined;
	}
	protected displayView(view) {
		if (!!view) {
			this._displayContainer.insert(view);
		}
	}

	protected renderMultipleViews() {
		this.clearDisplay();
		const values = <T[]>this.value || [];
		const views = values.map(value => this.getView(value));
		views.forEach(view => this.displayView(view));
	}
	// multiple
	protected _multiple = false;
	set multiple(m: boolean) {
		this._multiple = m;
		if (this._picker) {
			this._picker.multiple = m;
		}
	}
	get multiple() { return this._multiple; }
}

