		throw new Error('Unsupported');
	}

	public constructor(
		protected _vcr: ViewContainerRef,
		protected _cdr: ChangeDetectorRef,
		protected _templateRef: TemplateRef<LuForGroupContext<ILuGroup<ILuEstablishment, ILuLegalUnit>>>,
