	_emitCloseEvent(): void {
		this.close.emit();
	}

	_emitOpenEvent(): void {
		this.open.emit();
	}
	_emitHoveredEvent(hovered: boolean): void {
		this.hovered.emit(hovered);
	}

	onOpen() {
		this.focusFirstItem();
	}
	private focusFirstItem() {
		const firstItem = this._items[0];
		if (firstItem) {
			firstItem.focus()
		}
	}

