	displayClearer() {
		if (!!this.clearerEltRef) {
			this._renderer.appendChild(this.suffixEltRef.nativeElement, this.clearerEltRef.nativeElement);
		}
	}

	ngAfterViewInit() {
		super.ngAfterViewInit();
		this.displayClearer(); // dont keep
	}
}

